import React, { useEffect } from 'react';
import '../../App.css';
import Dashboards from '../HospitalDashbar';
import Footer from '../Footer';
import { db, collection, query, where, getDocs } from '../../firebase';

function HospitalDashboard({ hospital, handleLogout, updateNotificationCount}) {
    // const [notificationCount, setNotificationCount] = useState(0);

    useEffect(() => {
        const fetchNotifications = async () => {
            try {
                // Query to get the count of pending notifications
                const q = query(collection(db, 'requests'), where('status', '==', 'pending'));
                const querySnapshot = await getDocs(q);
                // Update state with the count of pending notifications
                const count = querySnapshot.size;
                updateNotificationCount(count);
                // setNotificationCount(querySnapshot.size);
                console.log('Notification count:', count);

            } catch (error) {
                console.error('Error fetching notifications:', error);
            }
        };

        fetchNotifications();
    }, [updateNotificationCount]);

    return (
        <>
            <Dashboards hospital={hospital} handleLogout={handleLogout} />
            <Footer />
        </>
    );
}

export default HospitalDashboard;
