import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FindingBlood = () => {
  const [map, setMap] = useState(null); // eslint-disable-next-line
  const [markers, setMarkers] = useState([]); // eslint-disable-next-line

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/data');
        const data = response.data;

        // Ensure data is an array
        if (!Array.isArray(data)) {
          throw new Error('Data received is not an array');
        }

        const google = window.google;
        if (!google) {
          throw new Error('Google Maps API not loaded');
        }

        const mapInstance = new google.maps.Map(document.getElementById('map'), {
          center: { lat: -1.2921, lng: 36.8219 },
          zoom: 10,
        });

        setMap(mapInstance);

        const newMarkers = await Promise.all(data.map(async (item) => {
          try {
            const geocoder = new google.maps.Geocoder();
            const geocodeResult = await geocoder.geocode({ address: item.Location });
            if (geocodeResult.length === 0) {
              throw new Error(`Geocode for '${item.Location}' failed`);
            }
            const location = geocodeResult[0].geometry.location;
            const marker = new google.maps.Marker({
              position: location,
              map: mapInstance,
              title: item.Location,
            });
            return marker;
          } catch (error) {
            console.error(`Error geocoding '${item.Location}':`, error);
            return null;
          }
        }));

        setMarkers(newMarkers.filter(marker => marker !== null));

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    const loadGoogleMapsApi = () => {
      if (!window.google) {
        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyD07cMGrAdZzFU1FexlCWLCWlPX-J3g6p4&libraries=places&async=1&defer=1`;
        script.async = true;
        script.defer = true;
        script.onload = () => {
          fetchData();
        };
        document.head.appendChild(script);
      } else {
        fetchData();
      }
    };

    loadGoogleMapsApi();

  }, []);

  return (
    <div>
      <div id="map" style={{ height: '400px', width: '100%' }}></div>
    </div>
  );
};

export default FindingBlood;
