import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import './BloodRecords.css';

const BloodRecords = () => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const [dashboardData, setDashboardData] = useState(null);

    useEffect(() => {
        fetch('http://127.0.0.1:5000/dashboard')
            .then(response => response.json())
            .then(data => setDashboardData(data))
            .catch(error => console.error('Error fetching dashboard data:', error));
    }, []);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    return (
        <div className="blood-records-container">
            <Sidebar 
                isOpen={isSidebarOpen} 
                toggleSidebar={toggleSidebar} 
                handleLogout={() => {}} 
            />
            <div className="blood-records-content">
                <button className="sidebar-toggler" onClick={toggleSidebar}>
                    â˜°
                </button>
                <h1>Blood Records Dashboard</h1>
                {dashboardData ? (
                    <div>
                        {/* Render your dashboard content here using dashboardData */}
                        <p>Blood Type Sums:</p>
                        <pre>{JSON.stringify(dashboardData.blood_type_sums, null, 2)}</pre>
                    </div>
                ) : (
                    <p>Loading dashboard data...</p>
                )}
            </div>
        </div>
    );
};

export default BloodRecords;
