import React, { useState, useEffect } from 'react';
import { getAuth, updatePassword } from 'firebase/auth';
import { db, collection, query, getDocs } from '../../firebase';
import './ProfileManage.css';

const ProfileAndRecords = () => {
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [error, setError] = useState('');
    const [recordsSummary, setRecordsSummary] = useState([]);
    const [loadingRecords, setLoadingRecords] = useState(true);

    // Fetch blood records summary
    useEffect(() => {
        const fetchRecordsSummary = async () => {
            try {
                const q = query(collection(db, 'bloodRecords'));
                const querySnapshot = await getDocs(q);
                const records = querySnapshot.docs.map(doc => doc.data());

                // Aggregate records by date and location
                const summary = records.reduce((acc, record) => {
                    const key = `${record.date}_${record.location}`;
                    if (!acc[key]) {
                        acc[key] = {
                            date: record.date,
                            location: record.location,
                            totalQuantity: 0
                        };
                    }
                    acc[key].totalQuantity += parseFloat(record.quantity) || 0;
                    return acc;
                }, {});

                setRecordsSummary(Object.values(summary));
                setLoadingRecords(false);
            } catch (error) {
                setError('Error fetching blood records');
            }
        };

        fetchRecordsSummary();
    }, []);

    // Handle password change
    const handlePasswordChange = async () => {
        try {
            const auth = getAuth();
            const user = auth.currentUser;
            
            if (!user) {
                throw new Error('No user is currently logged in.');
            }

            // Update the password
            await updatePassword(user, newPassword);
            alert('Password updated successfully');
            setOldPassword('');
            setNewPassword('');
        } catch (error) {
            setError('Error updating password: ' + error.message);
        }
    };

    return (
        <div className="profile-and-records">
            <div className="profile-management">
                <h1>Change Password</h1>
                {error && <p className="error">{error}</p>}
                <div className="profile-form">
                    <div>
                        <label>Old Password:</label>
                        <input
                            type="password"
                            value={oldPassword}
                            onChange={(e) => setOldPassword(e.target.value)}
                        />
                    </div>
                    <div>
                        <label>New Password:</label>
                        <input
                            type="password"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                        />
                    </div>
                    <button onClick={handlePasswordChange}>Change Password</button>
                </div>
            </div>

            <div className="blood-records-summary">
                <h1>Blood Records Summary</h1>
                {error && <p className="error">{error}</p>}
                {loadingRecords ? (
                    <p>Loading...</p>
                ) : recordsSummary.length === 0 ? (
                    <p>No records available.</p>
                ) : (
                    <table>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Location</th>
                                <th>Total Quantity</th>
                            </tr>
                        </thead>
                        <tbody>
                            {recordsSummary.map((record, index) => (
                                <tr key={index}>
                                    <td>{record.date}</td>
                                    <td>{record.location}</td>
                                    <td>{record.totalQuantity}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default ProfileAndRecords;
