import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './FindBloodHero.css';

const FindBlood = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/data')
            .then(response => {
                setData(response.data);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const processBloodData = (data, type) => {
        return data.reduce((acc, item) => acc + item[type], 0);
    };

    return (
        <div className="findblood-hero">
            <div className="findblood-content">
                <div className="data-boxes">
                    <div className="data-box">
                        <h3>Blood Demand</h3>
                        {data.map((item, index) => (
                            <p key={index}>{item.Location}: {item.Use}</p>
                        ))}
                        <p>Total Demand: {processBloodData(data, 'Use')}</p>
                    </div>
                    <div className="data-box">
                        <h3>Blood Donation</h3>
                        {data.map((item, index) => (
                            <p key={index}>{item.Location}: {item.Donate}</p>
                        ))}
                        <p>Total Donation: {processBloodData(data, 'Donate')}</p>
                    </div>
                </div>
                {/* Add your chart or graph component here if needed */}
            </div>
        </div>
    );
};

export default FindBlood;
