import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar.js';
import LoginNavbar from './components/LoginNavbar.js';
import Home from './components/pages/Home';
import AboutUs from './components/pages/AboutUs.js';
import FindBlood from './components/pages/FindBlood.js';
import Login from './components/pages/Login.js';
import Dashboard from './components/pages/Dashboard.js';
import DashboardNavbar from './components/DashboardNavbar.js';

function App() {
  const [hospital, setHospital] = useState(null);

  const handleLoginSuccess = (hospitalData) => {
    setHospital(hospitalData);
  };

  return (
    <Router>
      <NavWrapper hospital={hospital} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/aboutus" element={<AboutUs />} />
        <Route path="/findblood" element={<FindBlood />} />
        <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
        <Route path='/dash' element={<Dashboard hospital={hospital} />} />
      </Routes>
    </Router>
  );
}

function NavWrapper({ hospital }) {
  const location = useLocation();

  if (location.pathname === '/login') {
    return <LoginNavbar />;
  } else if (location.pathname === '/dash') {
    return <DashboardNavbar hospitalName={hospital ? hospital.name : 'Hospital'} />;
  } else {
    return <Navbar />;
  }
}

export default App;































