import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar.js';
import LoginNavbar from './components/LoginNavbar.js';
import Home from './components/pages/Home';
import AboutUs from './components/pages/AboutUs.js';
import FindBlood from './components/pages/FindBlood.js';
import Login from './components/pages/Login.js';
import Dashboard from './components/pages/Dashboard.js';
import DashboardNavbar from './components/DashboardNavbar.js';

const App = () => {
    const [hospital, setHospital] = useState(null);

    useEffect(() => {
        const savedHospital = localStorage.getItem('hospital');
        if (savedHospital) {
            setHospital(JSON.parse(savedHospital));
        }
    }, []);

    const handleLoginSuccess = (hospital) => {
        setHospital(hospital);
        localStorage.setItem('hospital', JSON.stringify(hospital));
    };

    const handleLogout = () => {
        setHospital(null);
        localStorage.removeItem('hospital');
    };

    return (
        <Router>
            <NavWrapper hospital={hospital} handleLogout={handleLogout} />
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/aboutus" element={<AboutUs />} />
                <Route path="/findblood" element={<FindBlood />} />
                <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
                <Route path="/dash" element={hospital ? <Dashboard hospital={hospital} handleLogout={handleLogout} /> : <Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

const NavWrapper = ({ hospital, handleLogout }) => {
    const location = useLocation();

    if (location.pathname === '/login') {
        return <LoginNavbar />;
    } else if (location.pathname === '/dash') {
        return <DashboardNavbar hospitalName={hospital ? hospital.name : 'Hospital'} />;
    } else {
        return <Navbar />;
    }
};

export default App;
