import React, { useState } from 'react';
import './Content.css';

function Content() {
  const [selectedContent, setSelectedContent] = useState('content1');

  const content = {
    content1: 'This is the content for header 1.',
    content2: 'This is the content for header 2.',
    content3: 'This is the content for header 3.'
  };

  return (
    <div className='home-page'>
      <div className='blur-background'>
        <img
          src='/images/img-6.png'
          alt='Small Image'
          className='small-image'
        />
        <div className='headers'>
          <h3 onClick={() => setSelectedContent('content1')}>Header 1</h3>
          <h3 onClick={() => setSelectedContent('content2')}>Header 2</h3>
          <h3 onClick={() => setSelectedContent('content3')}>Header 3</h3>
        </div>
        <div className='content'>
          <p>{content[selectedContent]}</p>
        </div>
      </div>
    </div>
  );
}

export default Content;
