import React, { useState } from 'react';
import Sidebar from './Sidebar';
import './BloodRecords.css';

const BloodRecords = () => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    return (
        <div className="blood-records-container">
            <Sidebar 
                isOpen={isSidebarOpen} 
                toggleSidebar={toggleSidebar} 
                handleLogout={() => {/* handle logout logic here */}} 
            />
            <div className="blood-records-content">
                <button className="sidebar-toggle" onClick={toggleSidebar}>
                    â˜°
                </button>
                <h1>Blood Records Dashboard</h1>
                <iframe
                    src="http://127.0.0.1:5000/dashboard/"
                    style={{ width: '100%', height: '800px', border: 'none' }}
                    title="Blood Records Dashboard"
                />
            </div>
        </div>
    );
};

export default BloodRecords;
