import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { db } from '../firebase';

const RequestForm = ({ show, handleClose, location }) => {
  const [hospital, setHospital] = useState('');
  const [phone, setPhone] = useState('');
  const [bloodType, setBloodType] = useState('');
  const [isEmergency, setIsEmergency] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const requestData = {
      hospital,
      phone,
      bloodType,
      isEmergency,
      location,
      timestamp: new Date().toISOString(),
    };

    try {
      await db.collection('requests').add(requestData);
      handleClose();
    } catch (error) {
      console.error('Error adding request:', error);
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Request Blood</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formHospital">
            <Form.Label>Hospital</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your hospital"
              value={hospital}
              onChange={(e) => setHospital(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="formPhone">
            <Form.Label>Phone Number</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your phone number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="formBloodType">
            <Form.Label>Blood Type</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter the blood type"
              value={bloodType}
              onChange={(e) => setBloodType(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="formEmergency">
            <Form.Check
              type="checkbox"
              label="Is it an emergency?"
              checked={isEmergency}
              onChange={(e) => setIsEmergency(e.target.checked)}
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default RequestForm;
