import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './FindBloodHero.css';

const FindBlood = () => {
    const [data, setData] = useState([]);
    const bloodTypes = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/data')
            .then(response => {
                setData(response.data);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const processGraphData = (data) => {
        const bloodTypeCounts = bloodTypes.map(type => {
            return data.filter(item => item['Blood Type'] === type).reduce((sum, item) => sum + item['Supplied'], 0);
        });

        return bloodTypeCounts;
    };

    return (
        <div className="findblood-hero">
            <div className="findblood-content">
                <div className="data-boxes">
                    {data.map((item, index) => (
                        <div className="data-box" key={index}>
                            <h3>{item.Location}</h3>
                            <p>Blood Type: {item['Blood Type']}</p>
                            <p>Supplied: {item.Supplied}</p>
                        </div>
                    ))}
                </div>
                <div className="chart-container">
                    <div className="bar-graph">
                        {processGraphData(data).map((count, index) => (
                            <div className="bar" key={index} style={{ height: `${count}px` }}>
                                <span className="bar-label">{bloodTypes[index]}</span>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default FindBlood;
