import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleCheck } from '@fortawesome/free-regular-svg-icons';
import './LoginForm.css';

const LoginForm = () => {
    const [hospitalName, setHospitalName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle login logic here
        console.log('Hospital Name:', hospitalName);
        console.log('Email:', email);
        console.log('Password:', password);
    };

    return (
        <div className="login-container">
            <div className="left-section">
                <img src="/images/img-12.png" alt="Logo" />
                <p>Central Login Server</p>
            </div>
            <div className="right-section">
                <h2>Login</h2>
                <form>
                    <div className="form-group">
                        <label htmlFor="hospitalName">Hospital Name</label>
                        <input type="text" id="hospitalName" className='login-input'
                            value={hospitalName}
                            onChange={(e) => setHospitalName(e.target.value)} placeholder='Type Here'
                            required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input type="email" id="email" className='login-input'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} placeholder='Enter email address'
                            required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input type="password" id="password" className='login-input'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} placeholder='Password'
                            required />
                    </div>
                    <button type='submit' className='login-button'>
                        Login <FontAwesomeIcon icon={faCircleCheck} />
                    </button>
                </form>
            </div>
        </div>
    );
}

export default LoginForm;
