import React, { useState, useEffect } from 'react';
import '../../App.css';
import Dashboards from '../Dashbar';
import Footer from '../Footer';
import { db, collection, query, where, getDocs } from '../../firebase';

function Dashboard({ hospital, handleLogout }) {
    const [notificationCount, setNotificationCount] = useState(0);

    useEffect(() => {
        const fetchNotifications = async () => {
            try {
                // Query to get the count of pending notifications
                const q = query(collection(db, 'requests'), where('status', '==', 'pending'));
                const querySnapshot = await getDocs(q);
                // Update state with the count of pending notifications
                setNotificationCount(querySnapshot.size);
                console.log('Notification count:', querySnapshot.size);
            } catch (error) {
                console.error('Error fetching notifications:', error);
            }
        };

        fetchNotifications();
    }, []);

    return (
        <>
            <Dashboards hospital={hospital} handleLogout={handleLogout} notificationCount={notificationCount} />
            <Footer />
        </>
    );
}

export default Dashboard;
