import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './FindBloodHero.css';

const FindBlood = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/data')
            .then(response => {
                setData(response.data);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    // const processBloodDemand = (data) => {
    //     const bloodTypes = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];
    //     const bloodTypeDemands = bloodTypes.map(type => {
    //         return data.filter(item => item['Blood Type'] === type).reduce((sum, item) => sum + item['Use'], 0);
    //     });

    //     return bloodTypeDemands;
    // };

    // const processBloodDonation = (data) => {
    //     const bloodTypes = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];
    //     const bloodTypeDonations = bloodTypes.map(type => {
    //         return data.filter(item => item['Blood Type'] === type).reduce((sum, item) => sum + item['Donate'], 0);
    //     });

    //     return bloodTypeDonations;
    // };

    return (
        <div className="findblood-hero">
            <div className="findblood-content">
                <div className="data-boxes">
                    <div className="data-box">
                        <h3>Blood Demand</h3>
                        {data.map((item, index) => (
                            <p key={index}>{item.Location}: {item.Use}</p>
                        ))}
                    </div>
                    <div className="data-box">
                        <h3>Blood Donation</h3>
                        {data.map((item, index) => (
                            <p key={index}>{item.Location}: {item.Donate}</p>
                        ))}
                    </div>
                </div>
                {/* <div className="chart-container">
                    <div className="bar-graph">
                        {processGraphData(data).map((count, index) => (
                            <div className="bar" key={index} style={{ height: `${count}px` }}>
                                <span className="bar-label">{bloodTypes[index]}</span>
                            </div>
                        ))}
                    </div>
                </div> */}
            </div>
        </div>
    );
};

export default FindBlood;
