import React, { useState } from 'react';
import Sidebar from './Sidebar';
import './Dashbar.css';
import RecordListWithModal from './RecordList';
import DashboardNavbar from './DashboardNavbar';

const Dashbar = ({ hospital, handleLogout, notificationCount }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    return (
        <div className="dashboard-container">
            <div className="dashboard">
                <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} handleLogout={handleLogout} />
                <div className="dashboard-header">
                    <button className="sidebar-toggle" onClick={toggleSidebar}>
                        â˜°
                    </button>
                    <DashboardNavbar hospitalName={hospital?.name || 'Hospital'} notificationCount={notificationCount} />
                    </div>
                    <div className="dashboard-content">
                    <h2>Welcome to: {hospital?.name || 'Hospital'}</h2> 
                    <RecordListWithModal />
                </div>
            </div>
        </div>
    );
};

export default Dashbar;
