import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FindingBlood = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/data');
        console.log('Data received:', response.data);
        setData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError(error);
      }
    };

    fetchData();

    const loadGoogleMapsApi = () => {
      if (!window.google) {
        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyD07cMGrAdZzFU1FexlCWLCWlPX-J3g6p4&libraries=places`;
        script.async = true;
        script.defer = true;
        script.onload = () => {
          console.log('Google Maps API script loaded');
          initializeMap();
        };
        script.onerror = (e) => {
          console.error('Error loading Google Maps API script:', e);
        };
        document.head.appendChild(script);
      } else {
        console.log('Google Maps API already loaded');
        initializeMap();
      }
    };

    const initializeMap = () => {
      if (window.google) {
        console.log('Google Maps API initialized');
        // eslint-disable-next-line no-unused-vars
        const map = new window.google.maps.Map(document.getElementById('map'), {
          center: { lat: -34.397, lng: 150.644 },
          zoom: 8,
        });
      } else {
        console.error('Google Maps API not available');
      }
    };

    loadGoogleMapsApi();

    return () => {
      const googleMapsScript = document.querySelector('script[src*="maps.googleapis.com"]');
      if (googleMapsScript) {
        document.head.removeChild(googleMapsScript);
      }
    };
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div>
      <h1>Blood Supply and Demand Data</h1>
      <ul>
        {Array.isArray(data) && data.length > 0 ? (
          data.map((item, index) => (
            <li key={index}>
              {Object.entries(item).map(([key, value]) => (
                <div key={key}><strong>{key}:</strong> {value}</div>
              ))}
            </li>
          ))
        ) : (
          <div>No data available</div>
        )}
      </ul>
      <div id="map" style={{ height: '400px', width: '100%' }}></div>
    </div>
  );
};

export default FindingBlood;
